HashNode2::HashNode2() { Exist = 0; }
HashNode1::HashNode1() { Exist = 0; }
HashNode0::HashNode0() { Exist = 0; }
int char2int(char c) {
	if ('0' <= c && c <= '9')return c - '0';
	else if ('A' <= c && c <= 'Z')return c - 'A' + 10;
	else if ('a' <= c && c <= 'z')return c - 'a' + 36;
	else if (c == '(')return 62;
	else if (c == ')')return 63;
	return -1;
}
char int2char(int i) {
	if (0 <= i && i <= 9)return i + '0';
	else if (i <= 35)return i + 'A' - 10;
	else if (i <= 61)return i + 'a' - 36;
	else if (i == 62)return '(';
	else if (i == 63)return ')';
	else return '\0';
}
string DCgetAllNext(string _prefix) {
	string res, res2;
	int len = _prefix.length();
	if (len == 0) {
		res = "";
	}
	else if (len == 1) {
		HashNode0& node0 = hashnode0[char2int(_prefix[0])];
		if (node0.Exist == 0)res = "";
		else {
			for (int i = 0; i < SUM_OF_KEY_CHAR; i++)
				if (node0.hashnode1[i].Exist)
					res += int2char(i);
		}
	}
	else if (len == 2) {
		HashNode0& node0 = hashnode0[char2int(_prefix[0])];
		HashNode1& node1 = node0.hashnode1[char2int(_prefix[1])];
		for (int i = 0; i < SUM_OF_KEY_CHAR; i++)
			if (node1.hashnode2[i].Exist)
				res += int2char(i);
	}
	else {
		HashNode0& node0 = hashnode0[char2int(_prefix[0])];
		HashNode1& node1 = node0.hashnode1[char2int(_prefix[1])];
		HashNode2& node2 = node1.hashnode2[char2int(_prefix[2])];
		size_t nSize = node2.txt.size(); size_t pos = 0;
		//cout << _prefix << endl;
		_prefix.erase(0, 3);
		//cout << _prefix << endl;
		string mark = "#" + _prefix;
		while (1) {
			pos = node2.txt.find(mark, pos);
			if (pos == std::string::npos)break;
			else if (node2.txt[pos + len - 2] != '#')res += node2.txt[pos + len - 2];
			pos++;
		}
	}
	if (res.length() <= 1)res2 = res;
	else {
		char lastc = res[0];
		for (int i = 1; i < res.length(); i++) {
			if (lastc != res[i]) {
				res2 += lastc; lastc = res[i];
			}
		}res2 += lastc;
	}
	return res2;
}
void DCinsert(string _word) {
	int len = _word.length();
	if (len == 0) return;
	else if (len == 1) {
		hashnode0[char2int(_word[0])].Exist = 1;
	}
	else if (len == 2) {
		HashNode0& node0 = hashnode0[char2int(_word[0])];
		node0.Exist = 1;
		node0.hashnode1[char2int(_word[1])].Exist = 1;
	}
	else {
		HashNode0& node0 = hashnode0[char2int(_word[0])];
		node0.Exist = 1;
		HashNode1& node1 = node0.hashnode1[char2int(_word[1])];
		node1.Exist = 1;
		HashNode2& node2 = node1.hashnode2[char2int(_word[2])];
		node2.Exist = 1;
		node2.txt += "#";
		for (int i = 3; i < len; i++)
			node2.txt += _word[i];
		//cout << _word[0] << _word[1] << _word[2] << node2.txt; system("pause");
	}
}
