void DCdisplayAll() {
	for (int i = 0; i < SUM_OF_KEY_CHAR; i++) {
		HashNode0& node0 = hashnode0[i];
		if (node0.Exist) {
			for (int j = 0; j < SUM_OF_KEY_CHAR; j++) {
				HashNode1& node1 = node0.hashnode1[j];
				if (node1.Exist) {
					for (int k = 0; k < SUM_OF_KEY_CHAR; k++) {
						HashNode2& node2 = node1.hashnode2[k];
						if (node2.Exist) {
							cout << int2char(i) << int2char(j) << int2char(k);
							cout << node2.txt << endl;
						}
					}
				}
			}
		}
	}
}
int DCmakeChoice(string _prefix, vector<float>& weight) {
	int res = char2int('H'); char tmp;
	//std::cout << "目标前缀为：" << _prefix << endl;
	string possiblenext = DCgetAllNext(_prefix);//收集前缀_prefix所有可能的下一个字符
	//std::cout << "备选字母为：" << possiblenext << endl;
	for (int i = 0; i < weight.size(); i++) {	//收集模型计算出的所有概率大于0.1的字符
		if (weight[i] > 0.05) {
			tmp = int2char(i);
			if (possiblenext.find(tmp) != string::npos && weight[i] > weight[res])
				res = i;
		}
	}
	if (res == char2int('H')) {
		res = 0;
		for (int j = 0; j < weight.size(); j++)
			if (weight[res] < weight[j])
				res = j;
	}
	if (_prefix.length() == 0 && 36 <= res && res <= 61)
		res -= 26;
	return res;
}
